// package forLab01;// Three Stooges Application// Laureli M.// // program for Lab 01  import java.awt.*;// we need this one for Componentsimport java.awt.event.*;// we need this one for ActionListenerimport javax.swing.*;// we need this one for components 					// whose class name begins with a J  public class ThreeStoogesL1 extends JFrame {	private JLabel instructionsL; //label giving instructions to the user		private JButton moeB, larryB, curlyB, laureliB;  // declare references to buttons that											// correspond to each Stooge												private JLabel resultsL; // labels the scroll pane that controls the 							//test area where phrases appear 								private JTextArea resultsTA; // declare a reference to a text area object 								// where phrases will appear									private JScrollPane resultsSP; // declare a reference to a scroll pane object 									// that controls the text area										private Container myCP; // hold a reference to the content pane so that 							// getContentPane() is only done once								public ThreeStoogesL1() {		super("Three Stooges");		setSize(300,330);		myCP = getContentPane();		myCP.setLayout(new FlowLayout());		myCP.setBackground(Color.CYAN);				instructionsL = new JLabel("Press a Stooge button to get a phrase");		instructionsL.setOpaque(true);		instructionsL.setBackground(Color.WHITE);		myCP.add(instructionsL);				laureliB = new JButton("Laureli"); 		laureliB.setForeground(Color.BLACK);		laureliB.addActionListener(new LaureliBHandler());		myCP.add(laureliB); 				moeB = new JButton("Moe"); 		moeB.setForeground(Color.BLUE);		moeB.addActionListener(new MoeBHandler());		myCP.add(moeB); 				larryB = new JButton("Larry"); 		larryB.setForeground(Color.RED); 		larryB.addActionListener(new LarryBHandler());		myCP.add(larryB); 				curlyB = new JButton("Curly"); 		curlyB.setForeground(Color.GREEN); 		curlyB.addActionListener(new CurlyBHandler());		myCP.add(curlyB); 				resultsL = new JLabel("         Results         "); 		resultsL.setOpaque(true);		resultsL.setBackground(Color.yellow); 		myCP.add(resultsL);				resultsTA = new JTextArea(); 		resultsTA.setBackground(Color.PINK);		resultsSP = 			new JScrollPane(resultsTA,						JScrollPane.VERTICAL_SCROLLBAR_ALWAYS,						JScrollPane.HORIZONTAL_SCROLLBAR_NEVER);		resultsSP.setPreferredSize(new Dimension(200,200));		myCP.add(resultsSP);  		setVisible(true);		addWindowListener(new WindowAdapter() {			public void windowClosing(WindowEvent e) {				System.exit(0);			}//windowClosing		}); //end of definition of WindowAdapter and semicolon to end the line 	}//ThreeStoogesL1 constructor		class LaureliBHandler implements ActionListener {		public void actionPerformed(ActionEvent e) {			resultsTA.append("Well snap. \n");		}// actionPerformed	}// LaureliBHandler		class LarryBHandler implements ActionListener {		public void actionPerformed(ActionEvent e) {			resultsTA.append("Wise Guy!\n");		}// actionPerformed	}// LarryBHandler		class MoeBHandler implements ActionListener {		public void actionPerformed(ActionEvent e) {			resultsTA.append("Knuckle Heads!\n");		}// actionPerformed	}// MoeBHandler		class CurlyBHandler implements ActionListener {		public void actionPerformed(ActionEvent e) {			resultsTA.append("Nyuck Nyuck!\n");		}// actionPerformed	}// CurlyBHandler		public static void main (String args[]) {		ThreeStoogesL1 myAppF = new ThreeStoogesL1(); 	} //main method	}// ThreeStoogesL1 class		