// package forLab03;// Laureli M// date// Lab 03: Placing GUI components on the JFrame's content pane// without the assistance of a layout managerimport java.awt.*;//for original GUI stuffimport java.awt.event.*;//for event handling stuffimport javax.swing.*;//for swing GUI components  public class ThreeStoogesHorizontal extends JFrame{	private JButton moeB, larryB, curlyB; // the buttons for each stooge	private JLabel resultsL; // a label for the output scroll pane	private JTextArea resultsTA; // text area where output is displayed	private JScrollPane resultsSP;// scroll pane that controls output text area	private Container myCP;// the content pane of the JFrame	public ThreeStoogesHorizontal() {  		super("Three Stooges");		setLocation(100,150);		setSize(500,200);		myCP = getContentPane();		myCP.setLayout(null); // eliminates the default layout manager		myCP.setBackground(Color.white);				moeB = new JButton("Moe"); 		moeB.setForeground(Color.blue);		moeB.setLocation(25, 15);		moeB.setSize(80, 20);		moeB.addActionListener(new MoeBHandler());		myCP.add(moeB); 				larryB = new JButton("Larry"); 		larryB.setForeground(Color.red); 		larryB.setLocation(135, 15);		larryB.setSize(80, 20);		larryB.addActionListener(new LarryBHandler());		myCP.add(larryB); 				curlyB = new JButton("Curly"); 		curlyB.setForeground(new Color(0, 100, 0)); 		curlyB.setLocation(245, 15);		curlyB.setSize(80, 20);		curlyB.addActionListener(new CurlyBHandler());		myCP.add(curlyB); 				resultsL = new JLabel("Results", JLabel.CENTER);		resultsL.setOpaque(true); //labels are transparent by default. You need to setOpaque									// to true if you want the label to have a different 									// background color than the content pane		resultsL.setBackground(Color.yellow);		resultsL.setForeground(Color.blue);		resultsL.setLocation(25,50);		resultsL.setSize(300,20); 		myCP.add(resultsL);				resultsTA = new JTextArea(); 		resultsTA.setBackground(Color.orange);		resultsTA.setForeground(Color.cyan);		resultsSP = 			new JScrollPane(resultsTA,						JScrollPane.VERTICAL_SCROLLBAR_ALWAYS,						JScrollPane.HORIZONTAL_SCROLLBAR_AS_NEEDED);		resultsSP.setLocation(25,75);		resultsSP.setSize(300,100);		myCP.add(resultsSP);  		setVisible(true);		addWindowListener(new WindowAdapter() {			public void windowClosing(WindowEvent e) {				System.exit(0);			}//windowClosing		}); //end of definition of WindowAdapter and semicolon to end the line 	}//ThreeStoogesHorizontal constructor		class LarryBHandler implements ActionListener {		public void actionPerformed(ActionEvent e) {			resultsTA.append("Wise Guy!\n");		}// actionPerformed	}// LarryBHandler		class MoeBHandler implements ActionListener {		public void actionPerformed(ActionEvent e) {			resultsTA.append("Knuckle Heads!\n");		}// actionPerformed	}// MoeBHandler		class CurlyBHandler implements ActionListener {		public void actionPerformed(ActionEvent e) {			resultsTA.append("Nyuck Nyuck!\n");		}// actionPerformed	}// CurlyBHandler		public static void main (String args[]) {		ThreeStoogesHorizontal myAppF = new ThreeStoogesHorizontal(); 	} //main method		}// ThreeStoogesHorizontal class		